INSERT INTO `medicamentos` VALUES (1,'Acetazolamida','Tratamiento de glaucoma, epilepsia y mal de altura','250-1000 mg/día VO en 1-2 dosis','Insuficiencia hepática grave, hiponatremia, alergia a sulfas','Usar con precaución en insuficiencia renal, ancianos y diabéticos','Parestesias, somnolencia, pérdida apetito, litiasis renal','Interacciona con litio, salicilatos y otros diuréticos','Inhibidor anhidrasa carbónica','Tabletas 250 mg, solución inyectable'),(2,'Aciclovir','Infecciones por virus herpes simple y zóster','200-800 mg VO cada 4-8 h, IV en casos graves','Insuficiencia renal sin ajuste, alergia','Ajustar en insuficiencia renal y mantener buena hidratación','Náuseas, cefalea, erupción cutánea, nefrotoxicidad','Interacciona con probenecid, zidovudina y ciclosporina','Análogo nucleósido antiviral','Tabletas 200/400/800 mg, crema tópica 5%, solución IV'),(3,'Ácido acetilsalicílico','Analgésico, antipirético, antiagregante plaquetario','500-1000 mg cada 6-8 h, 75-325 mg/día como antiagregante','Úlcera péptica, hemorragia activa, niños con varicela o gripe','Precaución en asmáticos, anticoagulados y ancianos','Dispepsia, sangrado GI, tinnitus, alergia','Interacciona con warfarina, corticoides y AINEs','Inhibidor COX irreversible','Tabletas 81 mg, 325 mg, 500 mg'),(4,'Ácido alendrónico','Osteoporosis y enfermedad de Paget','70 mg semanal o 10 mg/día VO en ayunas','Hipocalcemia, esofagitis activa, incapacidad de estar erguido','Tomar con agua, en ayunas y esperar 30 min antes de comer','Dolor óseo, dispepsia, esofagitis, osteonecrosis mandibular rara','Interacciona con calcio y antiácidos (disminuye absorción)','Bisfosfonato oral','Tabletas 10 mg, 70 mg'),(5,'Áacido fólico','Anemia megaloblástica, prevención defectos tubo neural','0.4-5 mg/día VO','Hipersensibilidad','Puede enmascarar déficit vitamina B12','Raras: náuseas, reacciones cutáneas','Interacciona con metotrexato, trimetoprim y anticonvulsivos','Vitamina B esencial','Tabletas 1 mg, 5 mg'),(6,'Ácido fusídico','Infecciones cutáneas estafilocócicas','500 mg VO cada 8-12 h, tópica 2% crema','Alergia al fármaco','Precaución en insuficiencia hepática y uso prolongado','Náuseas, diarrea, elevación transaminasas','Interacciona con estatinas (riesgo rabdomiólisis)','Antibiótico esteroideo','Crema 2%, tabletas 500 mg, solución IV'),(7,'Ácido tranexámico','Hemorragias por hiperfibrinólisis, menorragia','500-1000 mg VO cada 8 h o IV','Trombosis activa, hematuria superior, alergia','Precaución en antecedentes de trombosis, insuficiencia renal','Náuseas, mareo, diarrea, riesgo trombótico','Interacciona con anticonceptivos, factor IX y t-PA','Antifibrinolítico sintético','Tabletas 500 mg, ampollas 100 mg/mL'),(8,'Ácido valproico','Epilepsia, trastorno bipolar, profilaxis migraña','10-15 mg/kg/día inicial, hasta 60 mg/kg/día','Enfermedad hepática, embarazo, pancreatitis','Monitorear función hepática y niveles plasmáticos','Somnolencia, aumento peso, alopecia, hepatotoxicidad','Interacciona con lamotrigina, warfarina, fenitoína','Antiepiléptico ácido graso','Tabletas/cápsulas 200-500 mg, jarabe, IV'),(9,'Ácido zoledrónico','Osteoporosis, hipercalcemia tumoral, metástasis óseas','4 mg IV cada 3-4 semanas o 5 mg anual','Hipocalcemia, insuficiencia renal grave, embarazo','Corregir calcio/vit D antes del inicio','Fiebre, dolor óseo, hipocalcemia, osteonecrosis mandibular','Interacciona con aminoglucósidos y otros nefrotóxicos','Bisfosfonato IV','Viales 4 mg/5 mL, 5 mg/100 mL'),(10,'Acitretina','Psoriasis severa y queratodermias','25-50 mg/día VO con alimentos','Embarazo, lactancia, insuficiencia hepática o renal grave','Evitar embarazo hasta 3 años después del tratamiento','Sequedad mucocutánea, hipertrigliceridemia, hepatotoxicidad','Interacciona con alcohol, tetraciclinas, metotrexato','Retinoide sistémico','Cápsulas 10 mg, 25 mg'),(11,'Albendazol','Helmintiasis intestinales y tisulares','400 mg dosis única, prolongado en quistes','Embarazo, lactancia, hipersensibilidad','Precaución en